import {
  Component,
  Input,
  Output,
  EventEmitter,
  OnInit,
  OnChanges,
  AfterViewInit,
} from '@angular/core';

@Component({
  selector: 'app-img',
  templateUrl: './img.component.html',
  styleUrls: ['./img.component.scss'],
})
export class ImgComponent implements OnInit, OnChanges, AfterViewInit {
  @Input() img: string = '';
  @Output() loaded = new EventEmitter<string>();
  imageDefault = './assets/images/default.jpg';

  constructor() {
    // before render
    console.log('constructor ', 'imgValue  =>', this.img);
  }

  ngOnChanges() {
    // before render
    // listen changes on inputs
    console.log('ngOnChanges', this.img);
  }

  ngOnInit(): void {
    // before render
    // fetch - asunc
    // once time
  }

  imgError() {
    this.img = this.imageDefault;
  }

  imgLoaded() {
    console.log('child event');
    this.loaded.emit(this.img);
  }
}
<img
  width="100px"
  (load)="imgLoaded()"
  (error)="imgError()"
  [src]="img"
  *ngIf="img; else elseImg"
/>
<ng-template #elseImg>
  <img [src]="imageDefault" />
</ng-template>
