import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-product',
  templateUrl: './product.component.html',
  styleUrls: ['./product.component.scss'],
})
export class ProductComponent {
  product = {
    name: 'Laptop',
    url: 'https://cdn.shopify.com/s/files/1/0101/2522/products/LD0005959734_d11abfc9-3919-4245-a1dc-cc283615c6d3_1600x.jpg?v=1657154270',
    price: 100,
  };
}
<img [src]="product.url" />
<h2>{{ product.price }}</h2>
<p>Name: {{ product.price }}</p>
