@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
export class AppComponent {
  title = 'store';
  text = 'Store';

  constructor() {}
}
<input placeholder="some value" [(ngModel)]="text" />
<app-img [img]="text"></app-img>
