import { Component, Input, Output, EventEmitter } from '@angular/core';
import { IProduct } from '../../../models/product.model';

@Component({
  selector: 'app-product',
  templateUrl: './product.component.html',
  styleUrls: ['./product.component.scss'],
})
export class ProductComponent {
  @Input() product: IProduct = {
    id: '',
    title: '',
    image: '',
    price: 0,
    category: '',
    description: ' ',
  };
  @Output() addProduct = new EventEmitter<IProduct>();

  addToCart() {
    this.addProduct.emit(this.product);
  }
}
<app-img [img]="product.image"></app-img>
<h2>{{ product.price | currency : "GTQ" }}</h2>
<p>{{ product.title | reverse }}</p>
<p appHighlight>{{ product.description }}</p>
<button (click)="addToCart()">Add to Card</button>
